mapper.select:
select 

from MANAGE_PROSPECT_IMAGE
where PROSPECT_ID = #{prospectId,jdbcType=DECIMAL}
and TYPE = #{type,jdbcType=DECIMAL}

mapper.insert:
INSERT INTO MANAGE_PROSPECT_IMAGE
(ID,PROSPECT_ID,FOLDER_NAME,FILE_NAME,TYPE,CREATE_TIME)
select
MANAGE_PROSPECT_IMAGE_SEQ.NEXTVAL,#{prospectId},a.*,sysdate
from (

	SELECT
	#{item.folderName},#{item.fileName},#{item.picType}
	FROM DUAL

)a

insert into MANAGE_PROSPECT_IMAGE (ID, PROSPECT_ID, FOLDER_NAME, 
  FILE_NAME, CREATE_TIME, TYPE
  )
values (MANAGE_PROSPECT_IMAGE_SEQ.NEXTVAL, #{prospectId,jdbcType=DECIMAL}, #{folderName,jdbcType=VARCHAR}, 
  #{fileName,jdbcType=VARCHAR}, #{createTime,jdbcType=DATE}, #{type,jdbcType=DECIMAL}
  )
